    }
    ll mul(ll a,ll b){
        return (a%mod * b%mod)%mod;
    }
    ll sub(ll a , ll b){
        return (a%mod - b%mod + mod)%mod;
    }
    int wtf[100009];
    int wtb[100009];
    int pre[100009];
    vector<int> pos[100009];
    ll solve(int l,int r){
        if(r<l){
            return 0ll;
        }
        else{
            ll mn = get_min(l,r);
            auto it = lower_bound(pos[mp[mn]].begin(),pos[mp[mn]].end(),l);
            int m = *it;

            m++;
            l++;
            r++;
            ll frw = wtf[m-1]-wtf[l-1];
            ll pr = pre[m-1]-pre[l-1];
            ll pr2 = pre[r]-pre[m-1];
            ll bck = wtb[r]-wtb[m-1];
            m--;
            r--;
            l--;


            frw = sub(frw,mul(l,pr));
            ll cntr1 = mul(r-m+1,frw);

            bck = sub(bck,mul(pr2,N-r-2));
             ll cntr2 = mul(m-l+1,bck);
            ll alr = mul(mn,add(cntr1,cntr2));
            ll ntalr = add(solve(l,m-1),solve(m+1,r));
            // cout<<l<<" "<<r<<" "<<alr<<endl;
            return add(alr,ntalr);
        }
    }
public:
