        for(int i=1;i<=n;i++){
            pre[i] = add(pre[i-1],s[i-1]);
            wtf[i] = add(wtf[i-1],mul(i,s[i-1]));
            wtb[i] = add(wtb[i-1],mul(n-i,s[i-1]));
        }
        return (int)(solve(0,n-1));
    }
