class Solution
{
    private final long mod = 1_000_000_007L;

    public int totalStrength(int[] ss)
    {
        long stg = 0L;
        int n = ss.length;
		// regular presums
        long[] presums = new long[n + 1];
        for (int i = 0; i < n; i++)
        {
            presums[i + 1] = presums[i] + ss[i];
        }
		// advanced presums between presums[0] until presums[i]
        long[] presums2 = new long[n + 1];
        for (int i = 1; i <= n; i++)
        {
            presums2[i] = presums2[i - 1] + presums[i];
        }
        int[] leftSmallerIdxes = new int[n];
        int[] rightSmallerOrEqualIdxes = new int[n];
        Deque<Integer> deque = new ArrayDeque<>();
        for (int i = 0; i < n; i++)
        {
            int s = ss[i];
            while (!deque.isEmpty() && ss[deque.peekLast()] >= s)
            {
                deque.pollLast();
            }
            leftSmallerIdxes[i] = deque.isEmpty() ? -1 : deque.peekLast();
            deque.addLast(i);
        }
        deque = new ArrayDeque<>();
        for (int i = n - 1; i >= 0; i--)
        {
            int s = ss[i];
            while (!deque.isEmpty() && ss[deque.peekFirst()] > s)
            {
                deque.pollFirst();
            }
            rightSmallerOrEqualIdxes[i] = deque.isEmpty() ? n : deque.peekFirst();
            deque.addFirst(i);
        }
        for (int i = 0; i < n; i++)
        {
            long sum = 0;
            int leftSmallerIdx = leftSmallerIdxes[i];
            int leftCount = i - (leftSmallerIdx + 1);
            long leftSum = (i == 0 ? 0 : (leftCount * presums[i] - (presums2[i - 1] - (leftSmallerIdx < 0 ? 0 : presums2[leftSmallerIdx])))) % mod;
            int rightSmallerOrEqualIdx = rightSmallerOrEqualIdxes[i];
            int rightCount = rightSmallerOrEqualIdx - (i + 1);
            long rightSum = (presums2[rightSmallerOrEqualIdx] - presums2[i + 1] - rightCount * presums[i + 1]) % mod;
            sum += (leftSum * (rightCount + 1) + rightSum * (leftCount + 1)) % mod;
            sum += (long)ss[i] * (long)(leftCount + 1) * (long)(rightCount + 1) % mod;
            stg += (long)ss[i] * sum % mod;
            stg %= mod;
        }
        return (int)stg;
    }
}
