class Solution {

    private static final int MOD = (int) 1e9 + 7;;

    public int totalStrength(int[] strength) {
        int len = strength.length;
        // prefix sum of prefix sum
        // O(N)
        long[] ps = new long[len + 1];
        long[] psps = new long[len + 1];
        for (int i = 0; i < len; ++i) {
            ps[i+1] = (ps[i] + strength[i]) % MOD;
            psps[i+1] = (psps[i] + ps[i+1]) % MOD;
        }
        // mono stack to find the immediate right smaller element
        int[] rSmall = new int[len];
        Arrays.fill(rSmall, len);
        Deque<Integer> monoStk = new ArrayDeque<>();
        for (int i = 0; i < len; ++i) {
            while (!monoStk.isEmpty() && strength[monoStk.peek()] > strength[i]) {
                rSmall[monoStk.pop()] = i;
            }
            monoStk.push(i);
        }
        // mono stack to find the immediate left smaller (or equal) element
        int[] lSmall = new int[len];
        Arrays.fill(lSmall, -1);
        monoStk.clear();
        for (int i = len - 1; i >= 0; --i) {
            while (!monoStk.isEmpty() && strength[monoStk.peek()] >= strength[i]) {
                lSmall[monoStk.pop()] = i;
            }
            monoStk.push(i);
        }
        // compute & combine contributions of each element
        // rCount * (psps[i] - psps[Math.max(0, lSmall[i])]) - lCount * (psps[rSmall[i]] - psps[i])
        long ans = 0;
        for (int i = 0; i < len; ++i) {
            int s = strength[i];
            int lCount = i - lSmall[i];
            int rCount = rSmall[i] - i;
            long total = lCount * (psps[rSmall[i]] + MOD - psps[i]) % MOD + MOD - rCount * (psps[i] + MOD - psps[Math.max(0, lSmall[i])]) % MOD;
            ans = (ans + (s * total) % MOD) % MOD;
        }
        return (int) ans;
    }
}
```

