    vector<int> st;
    int n;
    unordered_map<int, int> lastposition;

    void fillprefixes(vector<int>&strength){
        for(int i=0;i<n;i++){ 
            prefix1[i+1] = summ(prefix1[i], strength[i]);
            prefix2[i+1] = prefix1[i+1];
        }

        for(int i=n-1;i>=0;i--){
            prefix3[i+1] = summ(prefix3[i+2], strength[i]);
            prefix4[i+1] = prefix3[i+1];
        }

        for(int i=n;i>=1;i--) prefix4[i] = summ(prefix4[i], prefix4[i+1]);
        for(int i=1;i<=n;i++) prefix2[i] = summ(prefix2[i], prefix2[i-1]);
    }

    void fillstack(vector<int>&strength){
        for(int i=0;i<n;i++){
            if(st.size() == 0) st.push_back(i);
            else{
                if(strength[i] >= strength[st.back()]) st.push_back(i);
                else{ 
                    while(st.size() && strength[st.back()] > strength[i]){
                        rightside[st.back()] = i-1;
                        st.pop_back();
                    }
                    st.push_back(i);
                }
            }
         }

        while(st.size()){
            rightside[st.back()] = n-1;
            st.pop_back();
        }

        for(int i=n-1;i>=0;i--){
            if(st.size() == 0) st.push_back(i);
            else{
                if(strength[i] >= strength[st.back()]) st.push_back(i);
                else{ 
                    while(st.size() && strength[st.back()] > strength[i]){
                        leftside[st.back()] = i+1;
                        st.pop_back();
                    }
                    st.push_back(i);
                }
            }
        }

        while(st.size()){
            leftside[st.back()] = 0;
            st.pop_back();
        }

    }

    in summ(in a, in b){ return ((a * 1ll) + (b * 1ll))%mod; }

    in mull(in a, in b){ return ((a * 1ll)%mod * (b * 1ll)%mod)%mod; }

    in subb(in a, in b){ return summ(((a * 1ll)%mod - (b * 1ll)%mod)%mod, mod); }

    in getsumright(in a, in b){
        if(a > b) return 0;
        in ans = subb(prefix2[b+1], prefix2[a]);
        in diff = mull(prefix1[a], b-a+1);
        return subb(ans, diff);
    }

    in getsumleft(in a, in b){
        in ans = subb(prefix4[a+1], prefix4[b+2]);
        in diff = mull(prefix3[b+2], b-a+1);
        return subb(ans, diff);
    }

    int totalStrength(vector<int>& strength) {
        n = strength.size();
        prefix1 = vector<in>(n+2, 0);
        prefix2 = vector<in>(n+2, 0);
        prefix3 = vector<in>(n+2, 0);
        prefix4 = vector<in>(n+2, 0);

        fillprefixes(strength);
        fillstack(strength);

        in ans = 0;

        for(int i=0;i<n;i++){
            int left = leftside[i];
            int right = rightside[i];
            if(lastposition[strength[i]] == 0 && strength[0] != strength[i]){}
            else if(i != 0) left = max(leftside[i], lastposition[strength[i]]+1);
            lastposition[strength[i]] = i;

            in leftsum = getsumleft(left, i);
            in rightsum = getsumright(i+1, right);

            in leftnum = i - left + 1;
            in rightnum = right - i + 1;

            leftsum = mull(leftsum, rightnum);
            rightsum = mull(rightsum, leftnum);
            in totsum = summ(leftsum, rightsum);
            totsum = mull(totsum, strength[i]);
            ans = summ(ans, totsum);
        }

        return ans;

    }
};
