Just find left smaller and right smaller element for every element
**Note : Since, there may be duplicates in the array, so you must find (previous smaller or equal element and next smaller element) OR (previous smaller and next smaller or equal element)**

Then you need to find sum of all subarrays that include index (i)
For that, I have done 2 prefix sum named as pre and pre2
Its by observation

```
class Solution {
public:
    const unsigned int M = 1000000007;

    vector<int> rightSmaller(vector<int>&s){
        stack<int> h;
        vector<int> ans;
        h.push(s.size()-1);
        ans.push_back(s.size());
        for(int i = s.size()-2;i>=0;i--){
            while(h.size()!=0 && s[h.top()]>s[i]){
                h.pop();
            }
            if(h.size()==0){
                ans.push_back(s.size());
            }
            else{
                ans.push_back(h.top());
            }
            h.push(i);
        }
        reverse(ans.begin(),ans.end());
        return ans;
    }


    vector<int> leftSmaller(vector<int>&s){
        stack<int> h;
        vector<int> ans;
        ans.push_back(-1);
        h.push(0);
        for(int i = 1; i< s.size();i++){
            while(h.size()!=0 && s[h.top()]>=s[i]){
                h.pop();
            }
            if(h.size()==0){
                ans.push_back(-1);
            }
            else{
                ans.push_back(h.top());
            }
            h.push(i);
        }
        return ans;
    }

    int totalStrength(vector<int>& strength) {

        vector<long long> pre(strength.size()+1,0);
        for(int i = 0; i < strength.size();i++){
            pre[i+1] = (pre[i]+strength[i])%M;
        }
        vector<long long>pre2(strength.size()+2,0);
        for(int i = 0; i < pre.size();i++){
            pre2[i+1] = (pre2[i]+pre[i])%M;
        }

        vector<int> right = rightSmaller(strength);
        vector<int> left = leftSmaller(strength);

int ans = 0;
        for(int i = 0; i < strength.size();i++){
            ans += ((pre2[right[i] + 1] - pre2[i + 1]) * (i - left[i]) % M + M * 2 - 
               (pre2[i + 1] - pre2[left[i] + 1]) * (right[i] - i) % M) % M * strength[i] % M;
        ans %= M;



        }
        return ans;

    }
};
