class Solution {
public:
    int totalStrength(vector<int>& strength) {
        int n = strength.size() ; 
        vector<long long > psum(n+1,0LL),ppsum(n+2,0LL) ; 
        vector<int>l(n,-1),r(n,n) ; 
        long long mod = 1e9+7;

        //calculating prefix sum as well as prefix of prefix sum 
        for(int i = 0 ; i<=n; i++)
        {
            if(i<n) psum[i+1] = (psum[i]+strength[i])%mod ; 
            ppsum[i+1] = (ppsum[i]+psum[i])%mod;
        }

        //found left smallest and right smallest  
        stack<int>s ;
        for(int i = 0 ;i<n;i++)
        {
            while(s.size() and strength[s.top()] >= strength[i])
            {
                r[s.top()] = i ; 
                s.pop();
            }
            l[i] = s.size()?s.top():-1 ;
            s.push(i);
        }

        /* some maths ,explained here-> [https://leetcode.com/problems/sum-of-total-strength-of-wizards/solutions/2062017/]()c-prefix-monotonic-stack-o-n-solution-with-thought-process/ */

        long long ans = 0 ; 

        for(int i = 0 ; i<n;i++)
        {
            ans += 
            (
                (
                    (ppsum[r[i]+1]-ppsum[i+1]) * (i-l[i]) %mod +
                    2*mod -
                    (ppsum[i+1]-ppsum[l[i]+1]) * (r[i]-i) %mod
                ) 
                * strength[i] %mod
            );

            ans %= mod ; 
        }

        return (int) ans;
    }
};
