class Solution {
public:
    int totalStrength(vector<int>& s) {
        int n = s.size();
        int m = 1e9+7;
        vector<int> pref(n+1);
        vector<int> dpref(n+2);
        for(int i = 1;i<=n;i++){
            pref[i]= (pref[i-1] + s[i-1])%m;
        }
        for(int i = 1;i<=n+1;i++){
            dpref[i]=(dpref[i-1] + pref[i-1])%m;
        }
        stack<int> st;
        long long ans = 0;
        for(int i =0;i<=n;i++){
            while(!st.empty() and (i==n || s[st.top()]>s[i])){
                long long mi = st.top();
                st.pop();
                //fromula - [dpref[right +1] - dpref[i+1]]*(i-Left) - [dpref[i+1] - dpref[left+1]]*(right-i)
                long long lb = st.empty()?-1:st.top(); //first term
                long long ft = 1L*(dpref[i+1]- dpref[mi+1]) % m; 
                ft = (ft * (mi-lb) ) % m;
                long long st = 1L * (dpref[mi + 1] - dpref[lb+1]) %m;
                st = (st * (i - mi)) % m;
                long long tans = 1L * ((ft-st) + m)%m;
                tans = (tans * s[mi])%m;
                ans = (ans + tans)%m;
                ans%=m;
            }
            st.push(i);
        }
        return ans;
    }
};
