    memset(presums, 0, sizeof(presums));
    for (int i = 0; i < n; ++i) {
      presums[i + 1] = (presums[i] + strength[i]) % mod;
    }

    int pre_presums[n + 2];
    memset(pre_presums, 0, sizeof(pre_presums));
    for (int i = 0; i < n + 1; ++i) {
      pre_presums[i + 1] = (pre_presums[i] + presums[i]) % mod;
    }

    int right[n];
    fill(right, right + n, n);
    int left[n];
    memset(left, -1, sizeof(left));
    stack<int> st;   // a monotonously-increasing stack
    for (int i = 0; i < n; ++i) {
      while (!st.empty() && strength[st.top()] > strength[i]) {
        right[st.top()] = i;
        st.pop();
      }
      if (!st.empty()) {
        left[i] = st.top();
      }
      st.emplace(i);
    }

    int ret = 0;
    for (int i = 0; i < n; ++i) {
      const int lhs = static_cast<int>(static_cast<long long>(i - left[i]) * ((mod + pre_presums[right[i] + 1] - pre_presums[i + 1]) % mod) % mod);
      const int rhs = static_cast<int>(static_cast<long long>(right[i] - i) * ((mod + pre_presums[i + 1] - pre_presums[left[i] + 1]) % mod) % mod);
      ret = (ret + static_cast<int>(static_cast<long long>(strength[i]) * ((mod + lhs - rhs) % mod) % mod)) % mod;
    }
    return ret;
  }
};
