/**
 * @param {number[]} strength
 * @return {number}
 */
const modulus = Math.pow(10,9) + 7;
var totalStrength = function(strength) {
    const segTree = new SegTree(strength);
    let l = 0, rInit = 1, r = rInit;
    const lastIdx = strength.length - 1;
    let result = segTree.getSum();

    while (r - l < strength.length) {
        const query = segTree.query(l, r);
        result += query[0] * query[1];
        l++;
        r++;
        if (r > lastIdx) {
            l = 0;
            rInit++;
            r = rInit;
            curMin = Infinity;
        }
    }

    return result % modulus;
};

class SegTree {
    arr = [];
    sum = 0;
    constructor(arr) {
        const n = arr.length;
        this.size = n;
        this.lastLevelNodeAmount = 2 ** Math.ceil(Math.log2(n));
        this.offset = this.lastLevelNodeAmount - 1;
        for (let i = 0; i < n; i++) {
            this.sum += arr[i] * arr[i];
            this.update(i, arr[i]);
        }
    }
    getSum() {
        return this.sum;
    }
    update(idx, val) {
        const old = this.arr[this.offset + idx] ?? 0;
        const diff = val - old;
        this.arr[this.offset + idx] = [val, val];
        this._update(this.offset + idx, diff);
    }
    _update(idx, diff) {
        const parentIdx = Math.ceil(idx / 2) - 1;
        if (!this.arr[parentIdx]) this.arr[parentIdx] = [0, Infinity];
        const parent = this.arr[parentIdx];

        parent[0] = parent[0] + diff;
        const leftChild = this.arr[parentIdx * 2 + 1] ?? [Infinity];
        const rightChild = this.arr[parentIdx * 2 + 2] ?? [Infinity];
        parent[1] = Math.min((leftChild[1] ?? Infinity), (rightChild[1] ?? Infinity));

        if (parentIdx > 0) this._update(parentIdx, diff)
    }
    query = (i, j) => {
        return this._query(i, j, 0, this.lastLevelNodeAmount, 0);
    }
    _query(i, j, l, r, idx) {
        if (i > r || j < l) return 0;
        if (i <= l && j >= Math.min(this.size, r) - 1) return this.arr[idx];

        const mid = l + Math.floor((r - l) / 2);
        const firstChildIdx = 2 * idx + 1;
        const secondChildIdx = 2 * idx + 2;

        if (i >= mid) { // right
            return this._query(i, j, mid, r, secondChildIdx);
        } else if (j < mid) { // left
            return this._query(i, j, l, mid, firstChildIdx);
        }

        const leftQuery = this._query(i, j, l, mid, firstChildIdx);
        const rightQuery = this._query(i, j, mid, r, secondChildIdx);

        return [leftQuery[0] + rightQuery[0], Math.min(leftQuery[1], rightQuery[1])];
    }
}
