        const parentIdx = Math.ceil(idx / 2) - 1;
        if (!this.arr[parentIdx]) this.arr[parentIdx] = [0, Infinity];
        const parent = this.arr[parentIdx];

        parent[0] = parent[0] + diff;
        const leftChild = this.arr[parentIdx * 2 + 1] ?? [Infinity];
        const rightChild = this.arr[parentIdx * 2 + 2] ?? [Infinity];
        parent[1] = Math.min((leftChild[1] ?? Infinity), (rightChild[1] ?? Infinity));

        if (parentIdx > 0) this._update(parentIdx, diff)
    }
    query = (i, j) => {
        return this._query(i, j, 0, this.lastLevelNodeAmount, 0);
    }
    _query(i, j, l, r, idx) {
        if (i > r || j < l) return 0;
        if (i <= l && j >= Math.min(this.size, r) - 1) return this.arr[idx];

        const mid = l + Math.floor((r - l) / 2);
        const firstChildIdx = 2 * idx + 1;
        const secondChildIdx = 2 * idx + 2;

        if (i >= mid) { // right
            return this._query(i, j, mid, r, secondChildIdx);
        } else if (j < mid) { // left
            return this._query(i, j, l, mid, firstChildIdx);
        }

        const leftQuery = this._query(i, j, l, mid, firstChildIdx);
        const rightQuery = this._query(i, j, mid, r, secondChildIdx);

        return [leftQuery[0] + rightQuery[0], Math.min(leftQuery[1], rightQuery[1])];
    }
}
