class Solution {
public:
    int mod = 1e9+7;

    int totalStrength(vector<int>& strength) {
        int n = strength.size(), i;
        long long ans = 0;
        vector<int> left(n, -1), right(n, n);
        vector<long long> psum(n+1, 0), prefix(n+2, 0);
        for(i = 0; i < n; i++) psum[i+1] = (strength[i]+psum[i])%mod;
        for(i = 0; i <= n; i++) prefix[i+1] = (psum[i]+prefix[i])%mod;
        stack<int> store1, store2;
        for(i = 0; i < n; i++) {
            while(!store1.empty() && strength[store1.top()]>=strength[i]) store1.pop();
            if(!store1.empty()) left[i] = store1.top();
            store1.push(i);
        }
        for(i = n-1; i >= 0; i--) {
            while(!store2.empty() && strength[store2.top()]>strength[i]) store2.pop();
            if(!store2.empty()) right[i] = store2.top();
            store2.push(i);
        }
        for(i = 0; i < n; i++) {
            ans += ((prefix[right[i]+1]-prefix[i+1])*(i-left[i])%mod+mod-(prefix[i+1]-prefix[left[i]+1])*(right[i]-i)%mod)%mod*strength[i]%mod;
            ans %= mod;
        }
        return (ans+mod)%mod;
    }
};
