typedef int64_t LL;

class Solution {
public:
    const int MOD = int(1e9 + 7);

    vector<int> nextSmallerElementRight(vector<int>& arr) {
        int n = arr.size();
        vector<int> ret(n, n);
        stack<int> st;
        for (int i = 0; i < n; i++) {
            while (!st.empty() && arr[i] < arr[st.top()]) {
                ret[st.top()] = i;
                st.pop();
            }
            st.push(i);
        }
        return ret;
    }

    vector<int> nextSmallerElementLeft(vector<int>& arr) {
        int n = arr.size();
        vector<int> ret(n, -1);
        stack<int> st;
        for (int i = n-1; i >= 0; i--) {
            while (!st.empty() && arr[i] <= arr[st.top()]) {
                ret[st.top()] = i;
                st.pop();
            }
            st.push(i);
        }
        return ret;
    }

    vector<LL> makePrefixPrefixSum(vector<int>& arr) {
        int n = (int)arr.size();
        vector<LL> psum(n);
        for (int i = 0; i < n; i++)
            psum[i] = ((i-1 >= 0 ? psum[i-1] : 0) + arr[i]) % MOD;

        vector<LL> ppsum(n);
        for (int i = 0; i < n; i++)
            ppsum[i] = ((i-1 >= 0 ? ppsum[i-1] : 0) + psum[i]) % MOD;

        return ppsum;
    }

    int totalStrength(vector<int>& strength) {
        int n = strength.size();

        vector<int> right = nextSmallerElementRight(strength);
        vector<int> left = nextSmallerElementLeft(strength);
        vector<LL> ppsum = makePrefixPrefixSum(strength);

//         for (int& x : right) cout << x << " ";
//         cout << endl;

//         for (int& x : left) cout << x << " ";
//         cout << endl;

//         for (LL& x : ppsum) cout << x << " ";
//         cout << endl;

        LL ret = 0;
        for (int i = 0; i < n; i++) {
            int l = left[i], r = right[i];
            LL sum = (((ppsum[r-1] - (i-1 >= 0 ? ppsum[i-1] : 0)) * (i-l)) % MOD) + MOD;
            sum = (sum - (r - i) * ((i-1 >= 0 ? ppsum[i-1] : 0) - (l-1 >= 0 ? ppsum[l-1] : 0))) % MOD;
            ret = (ret + (sum * strength[i]) % MOD) % MOD;
        }

        return ret;
    }
};
