class Solution {
    public int totalStrength(int[] strength) {
        int length = strength.length;
        long[][] prefixSumArr = new long[length + 1][2]; // 0 for prefixSum for prefixSum, 1 for prefixSum;
        final int MOD = (int)(1e9 + 7);
        for (int i = 1; i <= length; i++) {
            prefixSumArr[i][1] = (strength[i - 1] + prefixSumArr[i - 1][1]) % MOD;
            prefixSumArr[i][0] = (prefixSumArr[i - 1][0] + prefixSumArr[i][1]) % MOD;
        }
        Deque<int[]> minStack = new LinkedList<>();
        long res = 0;
        long[][] subArr = new long[length][2];
        for (int i = 0; i < length; i++) {
            while (!minStack.isEmpty() && minStack.peek()[0] >= strength[i]) {
                minStack.pollFirst();
            }
            if (minStack.isEmpty()) {
                subArr[i][0] = ((long)(i + 1) * prefixSumArr[i + 1][1] - prefixSumArr[i][0]) % MOD;
                subArr[i][1] = i + 1;
            } else {
                int k = minStack.peek()[1];
                subArr[i][0] = ((long)(i - k) * prefixSumArr[i + 1][1] - (prefixSumArr[i][0] - prefixSumArr[k][0])) % MOD;
                subArr[i][1] = i - k;
            }
            minStack.addFirst(new int[]{strength[i], i});
        }
        minStack.clear();
        for (int i = length - 1; i >= 0; i--) {
            while (!minStack.isEmpty() && minStack.peek()[0] > strength[i]) {
                minStack.pollFirst();
            }
            if (minStack.isEmpty()) {
                long right = (prefixSumArr[length][0] - prefixSumArr[i + 1][0] - (length - 1 - i) * prefixSumArr[i + 1][1]) % MOD;
                long totSum = (subArr[i][0] * (length - i) + right * subArr[i][1]) % MOD;
                res = (res + totSum * strength[i]) % MOD;
            } else {
                int k = minStack.peek()[1];
                long right = 0;
                if (k - i > 1) {
                    right = (prefixSumArr[k][0] - prefixSumArr[i + 1][0] - (k - i - 1) * prefixSumArr[i + 1][1]) % MOD;
                }
                long totSum = (subArr[i][0] * (k - i) + right * subArr[i][1]) % MOD;
                res = (res + totSum * strength[i]) % MOD;
            }
            minStack.addFirst(new int[]{strength[i], i});
        }
        return (int)res;
    }
}
