class Solution {
    func totalStrength(_ strength: [Int]) -> Int {
        guard !strength.isEmpty else { return 0 }

        let MOD = 1_000_000_007
        let N = strength.count


        var prefixSum = Array(repeating: 0, count: N + 1)
        for i in 0..<N {
            prefixSum[i + 1] = (prefixSum[i] + strength[i]) % MOD
        }

        // prefixSum of prefixSum
        var prefixPrefixSum = Array(repeating: 0, count: N + 2)
        for i in 0...N {
            prefixPrefixSum[i + 1] = (prefixPrefixSum[i] + prefixSum[i]) % MOD
        }

        // left[i]: 
        var left = Array(repeating: -1, count: N)

        var stk: [Int] = []

        for i in 0..<N {
            while !stk.isEmpty && strength[i] <= strength[stk.last!] {
                stk.removeLast()
            }

            left[i] = stk.last ?? -1
            stk.append(i)
        }

        stk.removeAll()
        var right = Array(repeating: N, count: N)

        for i in (0..<N).reversed() {
            while !stk.isEmpty && strength[i] < strength[stk.last!] {
                stk.removeLast()
            }
            right[i] = stk.last ?? N
            stk.append(i)
        }

        var res = 0
        for i in 0..<N {
            res += ((prefixPrefixSum[right[i] + 1] - prefixPrefixSum[i + 1]) * (i - left[i]) % MOD + MOD * 2 - (prefixPrefixSum[i + 1] - prefixPrefixSum[left[i] + 1]) * (right[i] - i) % MOD) % MOD * strength[i] % MOD;
            res %= MOD
        }
        return res
    }
}
