class Solution {
    public int totalStrength(int[] stren) {
        long MOD = 1000000007;

        int N = stren.length;

        long[] p1 = new long[N];
        long[] p2 = new long[N];

        p1[0] = stren[0];
        p2[0] = stren[0];

        for(int i=1;i<N;i++) {

            p1[i] = p1[i-1] + stren[i];
            p2[i] = p2[i-1] + p1[i];
        }

        Stack<Long> stk = new Stack<Long>();
        long result = 0;

        int i = 0;

        // both right and left excluded
        // p1[right-1] - p1[left] + p1[right-2] - p1[left] + ... p1[current] - p1[left]
        // p2[right-1] - p2[curr-1] - p1[left]*(right - 1 - curr + 1)
        // for all left to current-1
        // (p2[right-1] - p2[current-1])*(current - left) - (p1[left] + p1[left+1] + .. p1[current -1])(right - curr)

        while(i <= N) {

            while(! stk.empty() && (i==N || stren[stk.peek().intValue()] >= stren[i])) {
                int cur = stk.pop().intValue();

                int left = -1;

                if(! stk.empty()) {
                    left = stk.peek().intValue();
                }

                int right = i;

                long pr = right > 0 ? p2[right - 1] : 0;
                long pl = left > 0 ? p2[left - 1] : 0;
                long pc = cur > 0 ? p2[cur - 1] : 0;

                //exluded right and left index
                long count1 = cur - 1 - left + 1;
                long count2 = right - 1 - cur + 1;

                long pp1 = (pr - pc)%MOD;
                long pp2 = (pc - pl)%MOD;

                long res = (stren[cur]) * ((pp1*count1)%MOD - (pp2*count2)%MOD + 2*MOD)%MOD;

                res = (res + MOD)%MOD;
                result = (result + res)%MOD;

            }

            stk.push((long)i++);
        }

        return (int)result;

    }
}
