class Solution {
    public int totalStrength(int[] arr) {
        int n = arr.length;
        Stack<Integer> stk = new Stack<>();
        int[] next = new int[n];
        int[] s = new int[n + 1];
        int[] sum = new int[n + 1];
        for (int i = 0; i < n; i++) {
            next[i] = -1;
            s[i + 1] = add(s[i], arr[i]);
            sum[i + 1] = add(sum[i], s[i + 1]);
        }
        for (int i = 0; i < n; i++) {
            if (stk.size() == 0) {
                stk.add(i);
            } else {
                int t = 0; 
                while (stk.size() > 0 && arr[stk.peek()] > arr[i]) {
                    int j = stk.pop(); 
                    next[j] = i;
                }
                stk.add(i);
            }
        }
        int[] prev = new int[n];
        int res = 0;
        int[] t = new int[n];
        for (int i = n - 1; i >= 0; i--) {
            int nextSmaller = next[i];
            int cur;
            if (nextSmaller != -1) {
                int d = minus(s[nextSmaller], s[i]);
                cur = add(prev[nextSmaller], 
                          mul(d, t[nextSmaller]), 
                          mul(arr[i], calc(sum, s, nextSmaller - 1, i)));
                t[i] = add(t[nextSmaller], mul(arr[i], nextSmaller - i));
            }  else {
                cur = mul(arr[i], calc(sum, s, n - 1, i));
                t[i] = add(0, mul(arr[i], n - i));
            }
            prev[i] = cur;
            res = add(res, cur);
        }
        return res;
    }

    private int calc(int[] sum, int[] s, int a, int b) {
        return minus(minus(sum[a + 1], sum[b]), mul(a + 1 - b, s[b]));
    }

    private int minus(int a, int b) {
        int mod = 1000000007;
        return (int) ((a*1L - b*1L + mod)%mod);
    }

    private int add(int a, int b, int c) {
        int mod = 1000000007;
        return (int) ((a*1L + b*1L + c*1L)%mod);
    }

    private int add(int a, int b) {
        int mod = 1000000007;
        return (int) ((a*1L + b*1L)%mod);
    }

    private int mul(int a, int b) {
        int mod = 1000000007;
        return (int) ((a*1L*b)%mod);
    }
}
