```
class Solution {
public:
    const int mod = 1e9 + 7; 
    int totalStrength(vector<int>& a) {
        vector<long long> ss(a.begin(),a.end());
        vector<long long> sums(ss.size());
        sums[0] = ss[0];
        for(int i = 1;i<ss.size();i++) {
            sums[i] = sums[i-1] + ss[i];
        }
        auto sum = [&](int l, int r) {
            long long ret = 0; 
            ret = sums[r] - sums[l] + ss[l];
            return ret % mod; 
        };

        vector<long long> fs(a.size() + 1);
        long long x = 0; 
        for(int i = a.size()-1;i>=0;i--) {
            x += a[i];
            fs[i] = fs[i+1] + x; 
        }
        auto f = [&](int l, int r) {
            if(r == ss.size() - 1) {
                return fs[l] % mod;
            }
            long long ret = fs[l] - fs[r+1];
            ret -= ((r - l + 1) * sum(r+1, ss.size()-1)) % mod; 
            ret += mod; 
            ret %= mod; 
            return ret; 
        };
        stack<int> s; 
        s.push(-1);
        long long v = 0; 
        long long tmp = 0; 
        long long ret = 0; 
        for(int i = 0;i<ss.size();i++) {
            while(s.top() != -1 && ss[s.top()] >= ss[i]) {
                int t = s.top(); 
                s.pop(); 
                long long delta = f(s.top()+1,t);
                tmp -= (delta * ss[t]) % mod; 
                tmp += mod; 
                v -= ((t - s.top()) * ss[t]) % mod; 
                v += mod; 
                v %= mod; 
                tmp -= (v * sum(s.top()+1,t)) % mod;
                tmp += mod; 
                tmp %= mod; 
            }
            long long delta = f(s.top()+1,i);
            tmp = tmp + v * sum(s.top()+1,i) + delta * ss[i];
            v += (ss[i] * (i - s.top())) % mod;
            v %= mod; 
            s.push(i);
            tmp %= mod; 
            ret += tmp; 
            ret %= mod; 
        }
        return ret % mod; 

    }
};
