class Solution {
    using integer = long long;
    static constexpr integer kMod = 1E9 + 7;
public:
    int totalStrength(vector<int>& vals) {
        vals.push_back(0);
        std::vector<integer> prefix_sum(vals.size() + 1, 0);
        for (int i = 0; i < vals.size(); ++i) {
            prefix_sum[i + 1] = (prefix_sum[i] + vals[i]) % kMod;
        }

        std::vector<integer> double_prefix_sum(prefix_sum.size() + 1, 0);
        for (int i = 0; i < prefix_sum.size(); ++i) {
            double_prefix_sum[i + 1] = (prefix_sum[i] + double_prefix_sum[i]) % kMod;
        }        

        integer total = 0;
        const auto increment_total = [&total](const integer val) {
            total = (total + val % kMod) % kMod;
        };

        std::deque<int> inds_ord;
        for (int i = 0; i < vals.size(); ++i) {
            const int curr = vals[i];
            while (!inds_ord.empty()) {
                const int mid = inds_ord.back();
                if (vals[mid] < curr) {
                    break;
                }
                inds_ord.pop_back();
                const int lhs = inds_ord.empty() ? 0 : (inds_ord.back() + 1);
                const int rhs = i - 1;
                const integer lhs_cnts = mid - lhs + 1;
                const integer rhs_cnts = rhs - mid + 1;
                const integer minimal = vals[mid];
                increment_total(((minimal * minimal) % kMod) * ((lhs_cnts * rhs_cnts) % kMod));

                {
                    const auto window_sum = 
                        ((prefix_sum[mid] * (lhs_cnts - 1)) % kMod + kMod
                        - ((double_prefix_sum[mid] - double_prefix_sum[lhs]) % kMod)) % kMod;
                    increment_total(((window_sum * minimal) % kMod) * rhs_cnts);
                }

                {
                    const auto window_sum = 
                        ((double_prefix_sum[rhs + 2] - double_prefix_sum[mid + 2]) % kMod + kMod
                        - ((prefix_sum[mid + 1] * (rhs_cnts - 1)) % kMod)) % kMod;
                    increment_total(((window_sum * minimal) % kMod) * lhs_cnts);
                }
            }
            inds_ord.push_back(i);
        }
        return total;
    }
};
