```
class Solution {
public:
    bool checkTree(TreeNode* root) {
      queue<TreeNode*>q;
      q.push(root);
      while(!q.empty()){
          int size = q.size();
          for(int i=0;i<size;i++){
          TreeNode* curr = q.front();
          q.pop();
          int parentValue = curr->val;
          int childValue = 0;
          int child = 0;
          if(curr->left){
              q.push(curr->left);
              childValue+=curr->left->val;
          }else{
              child++;
          }
          if(curr->right){
          q.push(curr->right);
          childValue+=curr->right->val;
          }else{
              child++;
          }
          if(child==2) continue;
          if(parentValue != childValue) return false;
          }
      }
        return true;
    }
};
```

