class Solution:
    def checkTree(self, root: Optional[TreeNode]) -> bool:
        d=root.val
        self.res=0
        def dsf(root):
            if(root==None):
                return 
            if(root.left==None and root.right==None):
                self.res+=root.val
            else:
                dsf(root.left)
                dsf(root.right)
        dsf(root)
        return self.res==d
```

