const isAnagram = function (str1, str2) {
  const firstStr = str1;
  const secondStr = str2;

  if (firstStr === secondStr) return true;
  if (firstStr.length !== secondStr.length) return false;

  const firstHashMap = {};
  const secondHashMap = {};

  for (let i = 0; i < firstStr.length; i++) {
    if (firstHashMap[firstStr[i]])
