class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
    /* 
      words[i-1] and words[i] are anagrams when:
        +) words[i-1].size() == words[i].size();
        +) occurrence of each letter in words[i] equals to that in words[i-1]
    */
    for (int i = 1; i < words.size();){
        // We iterate from 1 to the end of list
        map<char, int> mp1, mp2;
        // We use map to store the occurrence of each letter in words[i] and words[i-1]
        for(char &x : words[i-1]) mp1[x]++;
        for(char &x : words[i]) mp2[x]++;
        if(mp1 == mp2){
            // If words[i] and words[i-1] are anagrams, we delete words[i] from the list
            words.erase(words.begin()+i);
        }else i++;
    }
    return words;        
}
};
