class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        vector<string> ans;
        int sz=words.size();
        vector<bool> vis(sz, false);
        for(int i=0; i<sz; i++){
            if(vis[i]) continue;
            int t = 0;
            string tmp = words[i];
            sort(tmp.begin(), tmp.end());
            for(int j=i+1; j<sz; j++){
                if(t != j-i-1) break;
                if(words[i].length() == words[j].length() && vis[j] == false){
                    string tmp2 = words[j];
                    sort(tmp2.begin(), tmp2.end());
                    if(tmp == tmp2){
                        vis[j] = true;
                        t++;
                    }
                }
            }
            ans.push_back(words[i]);
        }
        return ans;
    }
};
