const isAnagram = (word1: string, word2: string): boolean => {
    if (word1.length !== word2.length) return false
    if (word1 === word2) return true

    for (let i = 0; i < word1.length; i++) {
        const letter = word1[i]
        word2 = word2.replace(letter, '')
    }

    return !word2
