class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        stack<string>s;
        s.push(words[0]);
        for(int i=1;i<words.size();i++){
            string a = words[i];
            string b = s.top();
            sort(b.begin(),b.end());
            sort(a.begin(),a.end());
            if(a==b){
                continue;
            }
            else{
                s.push(words[i]);
            }
        }
        vector<string>ans;
        while(!s.empty()){
            string a = s.top();
            ans.push_back(a);
            s.pop();
        }
        reverse(ans.begin(),ans.end());
        return ans;
    }
};
```

