    public List<String> removeAnagrams(String[] strs) {
        List<Integer> toBeRemoved = new ArrayList<>();
        List<String> res = new ArrayList<>();
        int prev = 0;
        int current = 1;
        while (current < strs.length) {
            if (isAnagram(strs[current], strs[prev]))
                toBeRemoved.add(current);
            else
                prev = current;

            current++;
        }
        for (int str = 0; str < strs.length; str++) {
            if (!toBeRemoved.contains(str)) {
                res.add(strs[str]);
            }
        }
