int partitionArray(vector<int>& nums, int k) {
    sort(begin(nums), end(nums));
    vector<int> subs{nums[0]};
    for (int i = 1; i < nums.size(); ++i)
        if (nums[i] - subs.back() > k)
            subs.push_back(nums[i]);
    return subs.size();
}
