    private int binarySearch(int[] nums, int key) {
        int lo = 0, hi = nums.length;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (nums[mid] <= key) {
                lo = mid + 1;
            }else {
                hi = mid;
            }
        }
        return lo;
    }
