class Solution {
    public int partitionArray(int[] nums, int k) {
        TreeSet<Integer> ts = new TreeSet<>();
        for(int num: nums){
           ts.add(num);
        }

        Integer start = ts.first();
        int count = 0;
        while(start!=null && ts.ceiling(start)!=null){
            int end = start+k;
            count++;
            if(ts.ceiling(end+1)==null)
                return count;
            if(ts.contains(end+1))
            start = end+1;
            else
            start = ts.ceiling(end+1);  
        }
        return count;
    }
}

