class Solution {
public:
    int partitionArray(vector<int>& nums, int k) {
        int ans = 1; //There will be atleast one such partition
        //start denotes the starting point of the partition
        //end denotes the ending point
        int start = 0, end = 0;

        //Sort and start traversing
        sort(nums.begin(),nums.end());
        while(end < nums.size()){
            if(nums[end]-nums[start] <= k){
                //If valid check for the next index
                end++;
            }
            else{
                //If invalid then move start to this index
                start = end;
                ans++;
            }
        }
        return ans;
    }
};
