    public int binarySearch(int nums[],int start,int target)
    {
        int n=nums.length;
        int end=n-1;int ans=n;

        while(start<=end)
        {
            int mid=start+(end-start)/2;

            if(nums[mid]>=target)
            {
                ans=mid;
                end=mid-1;
            }

            else
            start=mid+1;
        }

        return ans;
    }
