class Solution {
     public int partitionArray(int[] nums, int k) {
        return usePriorityQueue(nums, k);
    }

    public int usePriorityQueue(int[] nums, int k) {
     PriorityQueue<Integer> p = new PriorityQueue<>();
     for(int num : nums){
         p.offer(num);
     }
        int ans = 0;
        while(!p.isEmpty()){
            ans++;
            int val = p.poll();
            while(!p.isEmpty() && p.peek() - val <= k){
                p.poll();
            }
        }
        return ans ; 
    }
}
