class Solution {
public:
    int partitionArray(vector<int>& nums, int k) {
        int n=nums.size();
		if(n==1) return 1;

        sort(nums.begin(),nums.end());
        int cnt=0;
        int idx=0;
        int diff;

        for(int i=1;i<n;i++)
        {
            diff= abs(nums[idx] - nums[i]);
            if(diff>k){
                cnt++;
                idx = i;
                i--;
             }

       }
       if(diff<=k) cnt++;

        return cnt;

    }
};
```

