class Solution {
public:
    int partitionArray(vector<int>& nums, int k) {


        sort(nums.begin(),nums.end());
        int i=0,s=nums.size()-1;
        int cnt=0;
        while(i<nums.size()){

            int mid;
            int start=i,end=s;
            int res;

            while(start<=end){
                mid=start+(end-start)/2;

                if(nums[mid]-nums[i]==k){
                    res=mid;
                    start=mid+1;
                }
                else if(nums[mid]-nums[i]<k){
                    res=mid;
                    start=mid+1;
                }

                else{
                    end=mid-1;
                }
            }

            i=res+1;
            cnt++;

        }
        return cnt;
    }
};
