/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
const partitionArray = function (nums, k) {
  nums.sort((a, b) => a - b)
  let result = 0
  if (nums[nums.length - 1] - nums[0] <= k) {
    return result + 1
  }
  // console.log(nums)
  const find = (start = 0) => {
    let left = start
    let right = nums.length - 1
    let next = -1
    while (left <= right) {
      const middle = Math.floor((left + right) / 2)

      if (nums[middle] - nums[start] > k) {
        next = middle
        right = middle - 1
      } else {
        left = middle + 1
      }
    }
    if (next !== -1) {
      result += 1
      find(next)
    }
  }
  find(0)
  return result + 1
}

