class Solution {
public:

/*

    We have to find minimun number of partition ,so we have to  
    include maximum valid element in our partition and we have 
    to just focus on getting the minimum and maximum element in 
    the partition

*/
    int partitionArray(vector<int>& nums, int k) {

        int n=nums.size();
        int i=0;
        int cnt=0;
        //So that duplicates element can come together and we can easily include them in our partition
        sort(nums.begin(),nums.end());

        while(i<n){

            //Since we have sorted the array we just need to find the greatest valid element in the array and we will get our partition
            int target=k+nums[i];

            //Upper bound to get the last index of target(if array have duplicates element)
            int idx=upper_bound(nums.begin(),nums.end(),target)-nums.begin();

            //If target is there in array we have got out partition so our new possible partition can start from next index
            if(idx<n && nums[idx]==target){
                idx++;
            }

            //If target not found then upper bound will automaically give us index of next element so we dont need to do idx++
            i=idx;
            cnt++;
        }

        return cnt;

    }
};
```

