        int res=  0 ,  min  = nums[0]  , max =  nums[0];
        for( var el :nums){
            min = Math.min(el , min);
            max = Math.max(el  ,max);
            if( max - min > k){
                res++;
                min = el ; max = el; 
            }
        }
        return res+1;
    }
}
