class Solution {
    public int partitionArray(int[] nums, int k) {
        Stack<Integer> st=new Stack<>();
        int count=1;
        Arrays.sort(nums);
        for(int i=0;i<nums.length;i++){
            if(st.size()==0){
                st.push(nums[i]);

            }
            else if((int)Math.abs(st.peek()-nums[i])>k){
                st.clear();
                st.push(nums[i]);
                count=count+1;

            }

        }
        int resul=count;
        return resul;
    }
}
