class Solution {
public:
    int partitionArray(vector<int>& a, int k) {
        int n=a.size();
        int cnt=0;
        sort(a.begin(),a.end());
        for(int i=0;i<n;i++)
        {
            int pos=upper_bound(a.begin(),a.end(),a[i]+k)-a.begin();
            pos--;
            i=pos;
            cnt++;
        }
        return cnt;
    }
};
