class Solution {
public:
    vector<int> successfulPairs(vector<int>& ss, vector<int>& p, long long suc) {
        vector<int>s=ss;// TO MAKE USE OF ITS SORTED(DESCENDING) FORM .
        unordered_map<int,int>mpp;// TO STORE SPELL WITH THEIR SUCCESS.
        vector<int>ans; // FINAL RESULT WILL BE STORED .

        sort(p.begin(),p.end());

        sort(s.begin(),s.end(),greater<int>()); // SORTING IN DESCENDING .

        int n=s.size();int n2=p.size();
 // K IS USED FOR NEW INDEX FROM WHERE NEXT SPELL SHOULD CHECK IN POTION FOR THEIR SUCCESS .
        int k=0;

        for(int i=0;i<n;i++){

            bool flag=0;// for check if any spell is making success or not.

            for(int j=k;j<n2;j++){
                // 1LL for avoiding overflow of int .
                if(1LL*s[i]*p[j]>=suc){
                    k=j; // storing new index for next spell to traverse.
                    flag=1;
                    mpp[s[i]]=n2-j;// storing success of current spell.
                    break;
                }
            }
            // if spell is not making success ,break;
            if(flag==0){  break; } 
        }

        for(int i=0;i<n;i++){
            // if spell was making any success .It had stored in mpp.
            if(mpp.count(ss[i]))ans.push_back(mpp[ss[i]]);

            else ans.push_back(0);
        }

        return ans;
    }
};
