class Solution {
    public int[] successfulPairs(int[] arr, int[] arr1, long suc) {
        int[] res = new int[arr.length];
        Arrays.sort(arr1);
        int l, r, mid;
        long t;
        for (int i = 0; i < arr.length; i++) {
            l = 0;
            r = arr1.length - 1;
            while (l <= r) {
                mid = (l + r) / 2;
                t = arr[i];
                if (arr1[mid] * t >= suc) {
                    r = mid - 1;
                    if(r==-1){
                        res[i] = arr1.length;
                    }
                } else if (arr1[mid] * t < suc) {
                    if (mid + 1 < arr1.length && arr1[mid + 1] * t >= suc) {
                        res[i] = arr1.length - (mid + 1);
                        break;
                    }
                    l = mid + 1;
                }
            }
        }

        return res;
    }
}

