    public int[] successfulPairs(int[] spells, int[] potions, long success) {
        int n = spells.length, m = potions.length;
        Arrays.sort(potions);
        int[] pairs = new int[n];
        for (int i = 0; i < n; i++) {
            int startIdx = findStartIndex(spells[i], potions, success);
            pairs[i] = m - startIdx;
        }
        return pairs;
    }

    private int findStartIndex(int spell, int[] potions, long success) {
        int m = potions.length;
        int lo = 0, hi = m - 1;
        int startIdx = m;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            long prod = 1L * spell * potions[mid];
            if (prod >= success) {
                startIdx = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return startIdx;
    }
}
