class Solution {
public:
    // Sorting and Binary Search
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        int n = spells.size(), m = potions.size();
        sort(potions.begin(), potions.end()); // Sorting potions so that we can binary search on it
        vector<int> ans;
        for(int i = 0; i < n; i++) { // For every spell, binary search on the number of potions that can be used such that spells[i] * potions[j] >= success
            int l = 0, r = m - 1, c = m;
            while(l <= r) {
                int mid = l + (r - l) / 2;
                if((((long long)potions[mid]) * ((long long)spells[i])) >= success) { // If this particular potion satisfies the condition, then we know that all the potions from potions[mid] to potions[m - 1] should satisfy the condition as well. Hence, we now store this index in 'c' and proceed to find if any lower index in potions satisfy the condition or not
                    c = mid;
                    r = mid - 1;
                } else { // Condition is not satisfied. Hence, we proceed to a higher index to see if the condition is satisfied for any index
                    l = mid + 1;
                }
            }
            ans.push_back(m - c); // Since all potions from potions[c] to potions[m - 1] satisfy the condition, we have 'm - c' pairs for this particular spell
        }
        return ans;
    }
};
