//AS (N*M<=10e10 operations and that can't pass within a second)

class Solution {
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
      vector<int>v;
      for(int i=0;i<spells.size();i++){
          int count=0;
          int x =spells[i];
          for(int j=0;j<potions.size();j++){
              long long result= (1LL*x*potions[j]);
              if(result>=success){
                count++;
              } 
          }
      v.push_back(count);
      }
      return v;
    }
};

// Optimised no :1 Here we are using STL

// Let's write the optimised version
// Still here we are using the stl function to find lower_bound which internally implements binary search but interviewer in some case might not allow to use STL so in that case we will have to write lower_bound using bs internally by ourself.
class Solution {
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
    int m = spells.size();
    int n =potions.size();
    sort(begin(potions),end(potions));
    int maxPotionValue=potions[n-1];
    vector<int>ans;
    for(int i=0;i<m;i++){
        int spell=spells[i];
