        long long minPotion=ceil((1.0*success)/spell);
        if(minPotion>maxPotionValue) {
            ans.emplace_back(0);
            continue;
        }
        int index=lower_bound(begin(potions),end(potions),minPotion)-begin(potions);
        int count=n-index;
        ans.emplace_back(count);
    } 
    return ans ;  
    }
};
