class Trie:
    def __init__(self):
        self.child = {}

    def add(self, word):
        cur = self
        for c in word:
            if c not in cur.child:
                cur.child[c] = Trie()
            cur = cur.child[c]

        if '#' not in cur.child:
            cur.child['#'] = '#'

    def find(self, word):
        cur = self 

        for c in word:
            if c not in cur.child:
                return False
            cur = cur.child[c]

        return True 

    def findExact(self, word):
        cur = self 

        for c in word:
            if c not in cur.child:
                return False
            cur = cur.child[c]
        return '#' in cur.child 

class Encrypter:

    def __init__(self, keys: List[str], values: List[str], dictionary: List[str]):
        self.keydic = {e:i for i,e in enumerate(keys)}
        self.values = values
        self.trie = Trie()

        for word in dictionary:
            self.trie.add(word)

        self.map = defaultdict(list)

        for key, enco in zip(keys, values): # build the map from the eco to the initial word
            self.map[enco].append(key)


    def encrypt(self, word1: str) -> str:
        res = ''
        for c in word1:
            res += self.values[self.keydic[c]]
        return res


    def decrypt(self, word2: str) -> int:
        chunks, chunk_size = len(word2), 2
        word = [ word2[i:i+chunk_size] for i in range(0, chunks, chunk_size)]
        size = len(word)
        dp = [[] for _ in range(size)]

        res  = 0
