```C++
struct Trie {
    Trie* next[26];
    bool eof = false;
    Trie() {memset(next,0,sizeof(next));}
    void insert(string& s, int p) {
        if(p == s.length()) eof = true;
        else {
            if(!next[s[p]-'a']) next[s[p]-'a'] = new Trie();
            next[s[p]-'a']->insert(s, p + 1);
        }
    }
};
class Encrypter {
    unordered_map<char, int> k;
    vector<string> v;
    unordered_map<string, unordered_set<char>> mpv;
    Trie* t, *rt;

    vector<pair<bool, vector<Trie*>>> DP;
    vector<bool> vis;
public:
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {
        t = new Trie();
        rt = new Trie();
        v = values;

        for(int i = 0; i < keys.size(); i++)
            k[keys[i]] = i;
        for(int i = 0; i < values.size(); i++)
            mpv[values[i]].insert(keys[i]);
        for(auto& v : values)
            t->insert(v, 0);
        for(auto& d : dictionary) {
            auto rd = d;
            reverse(rd.begin(), rd.end());
            rt->insert(rd, 0);
        }
    }

    string encrypt(string word1) {
        stringstream ss;
        for(auto& w : word1) {
            ss<<v[k[w]];
        }
        return ss.str();
    }

    int decrypt(string word2) {
        int n = word2.length();
        DP = vector<pair<bool, vector<Trie*>>>(n + 1, {false, {}});
        DP.back() = {true, {rt}};

        vis = vector<bool>(n + 1, false);
        vis.back() = true;

        auto [_, s] = helper(word2, 0);
        int res = 0;
        for(auto& rtrie : s) {
            res += rtrie->eof;
        }

        return res;
    }

    pair<bool,vector<Trie*>> helper(string& w, int p) {
        if(vis[p]) return DP[p];
        vis[p] = true;
        string ss;
        vector<Trie*> res;
        Trie* trie = t;
        bool PO = false;
        for(int i = p; i < w.length(); i++) {
            if(!trie->next[w[i]-'a']) break;

            trie = trie->next[w[i]-'a'];
            ss += w[i];
            if(!trie->eof) continue;
            auto [po, comb] = helper(w, i + 1);
            if(!po) continue;

            for(auto& rtrie : comb) {
                for(auto ch : mpv[ss]) {
                    if(!rtrie->next[ch-'a']) continue;
                    PO = true;
                    Trie* nrt = rtrie->next[ch-'a'];
                    res.push_back(nrt);
                }
            }
        }
        return DP[p] = {PO, res};
    }
};

