class Encrypter {
public:
    set<string> isInDictionary;
    vector<string> value;
    vector<int> keyIndex;
    unordered_map<char,string> keyToValueMap;


    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {

        //allocate space to store indices of each character in keys.
        keyIndex.resize(26,INT_MAX);

        int n=keys.size();
        for(int i=0;i<n;i++)
            keyIndex[keys[i]-'a']=i;

        //assign value to values vector.
        value=values;


        // store the corresponding value string of each character in keys.
        for(int i=0;i<n;i++)
            keyToValueMap[keys[i]]=values[i];

        //save the strings in dictionary in a map.
        for(auto str:dictionary)
            isInDictionary.insert(str);
    }

    string encrypt(string word1) {
        string encryptedStr;
