class Encrypter {
    class Node {
        Node[] child = new Node[26];
        boolean isEnd = false;
        String word;
        Node () {
            //  Default
        }
    }
    //  this map is requied for encrypting the string
    HashMap<Integer, List<String>> charMap = new HashMap<>();
    //  this map is required for decrypting the string (to get the next valid or possible 
    //  characters of a string in dictionary)
    HashMap<String, List<Integer>> revMap = new HashMap<>();
    //  stores all string in dictionary
    HashSet<String> dict = new HashSet<>();
    //  root of the Trie
    Node root;
    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        for (String s : dictionary) {
            dict.add(s);
        }
        constructTrie(dictionary);
        for (int i = 0; i < keys.length; i++) {
            //  charMap
            if (!charMap.containsKey(keys[i] - 'a')) {
                charMap.put(keys[i] - 'a', new ArrayList<>());
            }
            charMap.get(keys[i] - 'a').add(values[i]);
            //  revMap
            if (!revMap.containsKey(values[i])) {
                revMap.put(values[i], new ArrayList<>());
            }
            revMap.get(values[i]).add(keys[i] - 'a');
        }
    }

    //  Trie for pruning (adding words on trie)
    private void constructTrie(String[] words) {
        root = new Node();
        for (String w : words) {
            Node curr = root;
            for (char c : w.toCharArray()) {
                if (curr.child[c - 'a'] == null) {
                    curr.child[c - 'a'] = new Node();
                }
                curr = curr.child[c - 'a'];
            }
            curr.isEnd = true;
            curr.word = w;
        }
    }

    public String encrypt(String word1) {
        StringBuilder answer = new StringBuilder("");
        for (char c : word1.toCharArray()) {
            if (!charMap.containsKey(c - 'a')) return "";
            answer.append(charMap.get(c - 'a').get(0));
        }
        return answer.toString();
    }

    public int decrypt(String word2) {
        Node curr = root;
        return helper(word2, curr, 0);
    }

    private int helper(String s, Node curr, int currIdx) {
        if (currIdx == s.length()) {
            //  at the end of the string and checking if this is present in the dict
            return dict.contains(curr.word) ? 1 : 0;
        }
        String key = s.substring(currIdx, currIdx + 2);
        List<Integer> next = revMap.getOrDefault(key, new ArrayList<>());
        int count = 0;
        for (int c : next) {
            if (curr.child[c] != null) {
                count += helper(s, curr.child[c], currIdx + 2);
            }
        }
        return count;
    }
}

